ll count_divisible(ll l, ll r, ll d) {
    ll first= (l%d==0) ? l : l+d-l%d;  // ç¬¬ä¸€ä¸ªèƒ½è¢« d æ•´é™¤çš„æ•°
    ll last=r-r%d;                         // æœ€åä¸€ä¸ªèƒ½è¢« d æ•´é™¤çš„æ•°
    if (first>last) return 0;                 // å¦‚æœæ²¡æœ‰è¿™æ ·çš„æ•°
    return (last-first)/d+1;
