    ll ans=0;
    // æšä¸¾æ‰€æœ‰å­é›†ï¼ˆåŒ…æ‹¬ç©ºé›†ï¼‰ï¼Œå…± 2^4 = 16 ä¸ª
    for(int mask=0;mask<(1<<4);mask++) {
        ll d=1;
        int cnt=0;                           // å­é›†ä¸­è´¨æ•°çš„ä¸ªæ•°
        for(int i=0;i<4;i++){
            if(mask&(1<<i)){
                d*=primes[i];
                cnt++;
