using namespace std;
#define ll long long
int main(){
    ll n; cin>>n;
    ll s=sqrt(n);
    if(s*s!=n) s++;
    ll last=n%s;
    for(int i=last;i>=1;i--) cout<<i<<" ";
    for(int j=1;j<=n/s;j++){
        for(int i=last+j*s;i>last+(j-1)*s;i--) cout<<i<<" ";
    }
}
